DROP TABLE IF EXISTS `strategy`;
CREATE TABLE `strategy`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '标题',
  `headimg` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '标题图片',
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '内容',
  `simple_show` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '内容概要',
  `go_time` datetime(0) NULL DEFAULT NULL COMMENT '出发时间',
  `day_sum` int(11) NULL DEFAULT NULL COMMENT '旅游天数',
  `expense` int(11) NULL DEFAULT NULL COMMENT '花费',
  `publish_user` int(11) NULL DEFAULT NULL COMMENT '发布者id',
  `publish_username` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '发布者姓名',
  `publish_time` datetime(0) NULL DEFAULT NULL COMMENT '出版时间',
  `post_time` datetime(0) NULL DEFAULT NULL COMMENT '最后回复时间',
  `address` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '地址',
  `main_address` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '主要地址',
  `status` tinyint(2) NULL DEFAULT 1 COMMENT '状态:1=待审核,2=审核通过,3=审核未通过',
  `readme` int(11) NULL DEFAULT 0 COMMENT '阅读量',
  `laud` int(11) NULL DEFAULT 0 COMMENT '点赞量',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 9 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

